mapper.update:
update restaurant
set rest_dislikes = #{restDislikes,jdbcType=NUMERIC}
where rest_id = #{restId,jdbcType=NUMERIC}

update restaurant
set rest_likes = #{restLikes,jdbcType=NUMERIC}
where rest_id = #{restId,jdbcType=NUMERIC}

update restaurant
set rest_likes = rest_likes + 1
where rest_id = #{restId,jdbcType=NUMERIC}

update restaurant
set rest_dislikes = rest_dislikes + 1
where rest_id = #{restId,jdbcType=NUMERIC}
